// src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./components/Home";
import SignUp from "./components/SignUp";
import Login from "./components/Login";
import EventCreationForm from "./components/EventCreationForm";
import { useNavigate } from "react-router-dom";

// PrivateRoute component checks authentication before rendering the route
const PrivateRoute = ({ element }) => {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  // If no token is present, redirect to the login page
  if (!token) {
    navigate("/login");
    return null;
  }

  // If token is present, render the protected route (EventCreationForm in this case)
  return element;
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/login" element={<Login />} />
        {/* Protecting EventCreationForm route */}
        <Route
          path="/EventCreationForm"
          element={<PrivateRoute element={<EventCreationForm />} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
