import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const EventDetails = () => {
  const { eventId } = useParams(); // Get the event ID from the URL
  const [event, setEvent] = useState(null);
  const [error, setError] = useState("");
  const [userId, setUserId] = useState(null); // Track the logged-in user ID
  const [isRSVPed, setIsRSVPed] = useState(false); // Track RSVP status
  const [message, setMessage] = useState("");

  // Fetch event details based on eventId
  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/events/${eventId}`);
        setEvent(response.data);
      } catch (error) {
        console.error("Error fetching event details:", error);
        setError("Failed to fetch event details.");
      }
    };

    fetchEventDetails();
  }, [eventId]);

  // Fetch user ID from localStorage
  useEffect(() => {
    const storedUserId = localStorage.getItem("userId");
    if (storedUserId) {
      setUserId(storedUserId);
    }
  }, []);

  // RSVP handler
  const handleRSVP = async () => {
    try {
      const response = await axios.post("http://localhost:5000/rsvp", {
        eventId,
        userId,
      });
      setMessage(response.data.message || "RSVP successful!");
      setIsRSVPed(true);
    } catch (err) {
      setMessage(err.response?.data?.message || "Failed to RSVP.");
    }
  };

  // Cancel RSVP handler
  const handleCancelRSVP = async () => {
    try {
      const response = await axios.post("http://localhost:5000/cancel-rsvp", {
        eventId,
        userId,
      });
      setMessage(response.data.message || "RSVP cancelled.");
      setIsRSVPed(false);
    } catch (err) {
      setMessage(err.response?.data?.message || "Failed to cancel RSVP.");
    }
  };

  // Render error or loading state
  if (error) return <div style={{ color: "red" }}>{error}</div>;
  if (!event) return <div>Loading event details...</div>;

  return (
    <div className="event-details" style={{ padding: "20px" }}>
      <h2>{event.title}</h2>
      <p>{event.description}</p>
      <p><strong>Date:</strong> {new Date(event.date).toLocaleString()}</p>
      <p><strong>Location:</strong> {event.location}</p>
      <p><strong>Ticket Type:</strong> {event.ticket_type}</p>

      {/* Display ticket availability if available */}
      {event.ticket_availability && (
        <p><strong>Tickets Available:</strong> {event.ticket_availability}</p>
      )}

      {/* RSVP buttons */}
      {userId ? (
        isRSVPed ? (
          <button onClick={handleCancelRSVP} style={{ backgroundColor: "red", color: "white" }}>
            Cancel RSVP
          </button>
        ) : (
          <button onClick={handleRSVP} style={{ backgroundColor: "green", color: "white" }}>
            RSVP
          </button>
        )
      ) : (
        <p style={{ color: "orange" }}>Login to RSVP.</p>
      )}

      {/* Display feedback message */}
      {message && <p style={{ color: "green", marginTop: "10px" }}>{message}</p>}
    </div>
  );
};

export default EventDetails;
