import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const EventListing = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://localhost:5000/events");
        setEvents(response.data);
      } catch (error) {
        console.error("Failed to fetch events:", error);
      }
    };

    fetchEvents();
  }, []);

  return (
    <div className="event-listing" style={{ padding: "20px" }}>
      <h2>Event List</h2>
      {events.length === 0 ? (
        <p>No events available.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {events.map((event) => (
            <li key={event.id} style={{ marginBottom: "16px", borderBottom: "1px solid #ddd", paddingBottom: "10px" }}>
              <h3>{event.title}</h3>
              <Link to={`/organizer-dashboard/event/${event.id}`} style={{ color: "blue", textDecoration: "underline" }}>
                View Details
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default EventListing;
